properties.widgets.SapOdp-batchsource:
{
  "outputs": [{
    "name": "schema",
    "widget-type": "schema",
    "widget-attributes": {
      "schema-types": [
        "int",
        "long",
        "double",
        "bytes",
        "string"
      ],
      "schema-default-type": "string"
    }
  }],
  "metadata": {"spec-version": "1.5"},
  "configuration-groups": [
    {
      "label": "Basic",
      "properties": [
        {
          "widget-type": "textbox",
          "name": "referenceName",
          "label": "Reference Name",
          "widget-attributes": {"placeholder": "Name used to identify this source for lineage"}
        },
        {
          "widget-type": "textbox",
          "name": "jco.client.client",
          "label": "SAP Client",
          "widget-attributes": {
            "max-length": "3",
            "placeholder": "Ex: 400"
          }
        },
        {
          "widget-type": "textbox",
          "name": "jco.client.lang",
          "label": "SAP Language",
          "widget-attributes": {
            "default": "EN",
            "placeholder": "Ex: DE, EN, JP etc."
          }
        },
        {
          "widget-type": "radio-group",
          "name": "connType",
          "label": "Connection Type",
          "widget-attributes": {
            "layout": "inline",
            "default": "directClient",
            "options": [
              {
                "id": "directClient",
                "label": "Direct (via SAP Application Server)"
              },
              {
                "id": "msgServer",
                "label": "Load Balanced (via SAP Message Server)"
              }
            ]
          }
        },
        {
          "widget-type": "textbox",
          "name": "jco.client.ashost",
          "label": "SAP Application Server Host",
          "widget-attributes": {"placeholder": "SAP Application Server (AS) Hostname or IP"}
        },
        {
          "widget-type": "textbox",
          "name": "jco.client.sysnr",
          "label": "SAP System Number",
          "widget-attributes": {"placeholder": "Ex: 01"}
        },
        {
          "widget-type": "textbox",
          "name": "jco.client.saprouter",
          "label": "SAP Router",
          "widget-attributes": {"placeholder": "SAP Router String"}
        },
        {
          "widget-type": "textbox",
          "name": "jco.client.mshost",
          "label": "SAP Message Server Host",
          "widget-attributes": {"placeholder": "SAP Message Server (MS) Hostname or IP"}
        },
        {
          "widget-type": "textbox",
          "name": "jco.client.msserv",
          "label": "SAP Message Server Service or Port Number",
          "widget-attributes": {"placeholder": "Ex: sapms02 or 3602"}
        },
        {
          "widget-type": "textbox",
          "name": "jco.client.r3name",
          "label": "SAP System ID (SID)",
          "widget-attributes": {"placeholder": ""}
        },
        {
          "widget-type": "textbox",
          "name": "jco.client.group",
          "label": "SAP Logon Group Name",
          "widget-attributes": {
            "default": "PUBLIC",
            "placeholder": "Ex: PUBLIC"
          }
        },
        {
          "widget-type": "textbox",
          "name": "sapSourceObjName",
          "label": "SAP ODP Datasource Name",
          "widget-attributes": {"placeholder": "Ex: 2LIS_02_ITM"},
          "plugin-function": {
            "widget": "outputSchema",
            "method": "POST",
            "plugin-method": "getSchema"
          }
        },
        {
          "widget-type": "radio-group",
          "name": "extractType",
          "label": "Extract Type",
          "widget-attributes": {
            "layout": "inline",
            "default": "Full",
            "options": [
              {
                "id": "Full",
                "label": "Full (All Data)"
              },
              {
                "id": "Sync",
                "label": "Sync (Automatic selection based on previous execution)"
              }
            ]
          }
        }
      ]
    },
    {
      "label": "Credentials",
      "properties": [
        {
          "widget-type": "textbox",
          "name": "jco.client.user",
          "label": "SAP Logon Username"
        },
        {
          "widget-type": "password",
          "name": "jco.client.passwd",
          "label": "SAP Logon Password"
        }
      ]
    },
    {
      "label": "SAP JCo Details",
      "properties": [
        {
          "widget-type": "textbox",
          "name": "gcpProjectId",
          "label": "GCP Project ID",
          "widget-attributes": {"default": "auto-detect"}
        },
        {
          "widget-type": "textbox",
          "name": "gcsPath",
          "label": "SAP JCo Library GCS Path",
          "widget-attributes": {"placeholder": "Google Cloud Storage path having SAP JCo libraries. Ex: gs://cdf-sap-dependent-files/v3_0_20"}
        }
      ]
    },
    {
      "label": "Advanced",
      "properties": [
        {
          "widget-type": "textbox",
          "name": "subscriberName",
          "label": "SAP ODP Subscriber Name",
          "widget-attributes": {
            "placeholder": "Ex: Request_Material",
            "maxLength": "32"
          }
        },
        {
          "widget-type": "keyvalue",
          "name": "filterOptionsEq",
          "label": "Filter Options (Equal)",
          "widget-attributes": {
            "kv-delimiter": ":",
            "key-placeholder": "MTART",
            "value-placeholder": "FERT",
            "delimiter": ",",
            "showDelimiter": "false"
          }
        },
        {
          "widget-type": "keyvalue",
          "name": "filterOptionsRange",
          "label": "Filter Options (Range)",
          "widget-attributes": {
            "kv-delimiter": ":",
            "key-placeholder": "MATNR",
            "value-placeholder": "ACT-DCD-00 AND ACT-DCD-12",
            "delimiter": ",",
            "showDelimiter": "false"
          }
        },
        {
          "widget-type": "number",
          "name": "rowsToFetch",
          "label": "Number of Rows to Fetch",
          "widget-attributes": {"min": "0"}
        },
        {
          "widget-type": "number",
          "name": "numSplits",
          "label": "Number of Splits to Generate",
          "widget-attributes": {"min": "0"}
        },
        {
          "widget-type": "number",
          "name": "packageSize",
          "label": "Package Size (in KB)",
          "widget-attributes": {"min": "0"}
        }
      ]
    }
  ],
  "display-name": "SAP ODP",
  "filters": [
    {
      "condition": {
        "property": "connType",
        "value": "directClient",
        "operator": "equal to"
      },
      "name": "directClient",
      "show": [
        {
          "name": "jco.client.ashost",
          "type": "property"
        },
        {
          "name": "jco.client.sysnr",
          "type": "property"
        },
        {
          "name": "jco.client.saprouter",
          "type": "property"
        }
      ]
    },
    {
      "condition": {
        "property": "connType",
        "value": "msgServer",
        "operator": "equal to"
      },
      "name": "msgServer",
      "show": [
        {
          "name": "jco.client.mshost",
          "type": "property"
        },
        {
          "name": "jco.client.msserv",
          "type": "property"
        },
        {
          "name": "jco.client.r3name",
          "type": "property"
        },
        {
          "name": "jco.client.group",
          "type": "property"
        }
      ]
    }
  ]
}
